import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate, Link } from "react-router-dom";
import io from "socket.io-client";
import DeleteButton from "./DeleteButton";
const socket = io.connect(":8000");

const ViewPet = () => {
      const [pet, setPet] = useState({
        name: "",
        type: "",
        description: "",
        skillOne: "",
        skillTwo: "",
        skillThree: "",
      });

      const [pets, setPets] = useState([]);

      useEffect(() => {
        axios
          .get("http://localhost:8000/api/pets/")
          .then((res) => {
            console.log(res);
            setPets(res.data.pets);
          })
          .catch((err) => console.error(err));
      }, []);
      const removeFromDom = (petId) => {
        setPets(pets.filter((pet) => pet._id !== petId));
      };
    return (
      <div className="container mt-5 p-2">
        <div className="d-flex justify-content-between">
          <h1 className="display-4">Pet Shelter</h1>
          <div>
            <Link to="/" className="btn btn-outline-dark m-2">
              Back to Home
            </Link>
          </div>
        </div>
        <div className="d-flex justify-content-between">
          <h1 className="">Details about:</h1>
          <div>
            <DeleteButton
              petId={pet._id}
              successCallback={() => removeFromDom(pet._id)}
            />
          </div>
        </div>
            <div className="row border m-3">
                <div className="col">

                </div>
                <div className="col">
                    {pet.type}
                </div>

        </div>
      </div>
    );
};

export default ViewPet;
